        func setupView() {
            setupViewCalled = true
        }
        
        func getPhotosCount() -> Int {
            getPhotosCountCalled = true
            return 10
        }
        
        func getPhotoCellFor(index: Int) -> Photos.Models.PhotoCellModel {
            getPhotoCellForCalled = true
            return Photos.Models.PhotoCellModel()
        }
